import React, { useEffect, useState } from "react";
import { BrowserRouter, Routes, Route, useNavigate } from "react-router-dom";
import HomePage from "../Pages/HomePage";
import LoginPage from "../Pages/LoginPage";
import ProtectedRoutes from "../Utils/ProtectedRoutes";
import SignUpPage from "../Pages/SignUpPage";
import SystemAdminPage from "../Pages/SystemAdminPage";
import UsersAdminPage from "../Pages/UsersAdminPage";
//state..

function App() {
  const [email, setEmail] = useState('');
  const [userType, setUserType] = useState('');
  const navigate = useNavigate();
  useEffect(() => {
    if (userType === '') {
      Navigate('/login');
    }
  });
  return (
    <BrowserRouter>
      <Routes>
        <Route element={<ProtectedRoutes />}>
          {userType === 'user' && <Route path='/user' element={<HomePage />} />}
          {userType === 'systemadmin' && <Route path="/systemadmin" element={<SystemAdminPage />} />}
          {userType === 'useradmin' && <Route path="/useradmin" element={<UsersAdminPage />} />}
        </Route>
        {userType === '' && <>
          <Route path='/login' element={<LoginPage email={email} setEmail={setEmail} setUserType={setUserType} />} />
          <Route path='' element={<LoginPage email={email} setEmail={setEmail} setUserType={setUserType} />} />
          <Route path='/signup' element={<SignUpPage />} />
        </>
        }
      </Routes>
    </BrowserRouter>
  );
}

export default App;
